'use strict';

var CompaniesQueries = {
  'select companies count': 'SELECT count(*) FROM companies.e_companies WHERE is_deleted = \'N\';',
  'select companies': 'SELECT id, short_name AS "shortName", long_name AS "longName", full_name AS "fullName" FROM companies.e_companies WHERE is_deleted = \'N\' ORDER BY id ASC;',
  'select company': 'SELECT id, short_name AS "shortName", long_name AS "longName", full_name AS "fullName" FROM companies.e_companies WHERE is_deleted = \'N\' AND id = $1;',
  'insert company': 'INSERT INTO companies.e_companies (id, short_name, long_name, full_name) VALUES ($1, $2, $3, $4) RETURNING id;',
  'update company': 'UPDATE companies.e_companies SET id = $1, short_name = $2, long_name = $3, full_name = $4 WHERE id = $1 RETURNING id;',
  'delete company': 'UPDATE companies.e_companies SET is_deleted = \'Y\' WHERE id = $1 RETURNING id;',
  'restore company': 'UPDATE companies.e_companies SET is_deleted = \'N\' WHERE id = $1 RETURNING id;'
};

module.exports = CompaniesQueries;
