'use strict';

var PersonsQueries = {
  'select persons count': 'SELECT count(*) FROM persons.e_persons WHERE is_deleted = \'N\';',
  'select persons': 'SELECT id, last_name AS "lastName", first_name AS "firstName", middle_name AS "middleName", dob, gender_id AS "genderID" FROM persons.e_persons WHERE is_deleted = \'N\' ORDER BY id ASC;',
  'select person': 'SELECT id, last_name AS "lastName", first_name AS "firstName", middle_name AS "middleName", dob, gender_id AS "genderID" FROM persons.e_persons WHERE is_deleted = \'N\' AND id = $1;',
  'insert person': 'INSERT INTO persons.e_persons (id, last_name, first_name, middle_name, dob, gender_id) VALUES ($1, $2, $3, $4, $5, $6) RETURNING id;',
  'update person': 'UPDATE persons.e_persons SET id = $1, last_name = $2, first_name = $3, middle_name = $4, dob = $5, gender_id = $6 WHERE id = $1 RETURNING id;',
  'delete person': 'DELETE persons.e_persons id = $1 RETURNING id;',
  'restore person': 'UPDATE persons.e_persons SET is_deleted = \'N\' WHERE id = $1 RETURNING id;'
};

module.exports = PersonsQueries;
