'use strict';

var dictRouter = require('express').Router(),
DBQueries = require('../db/DBQueries.js'),
DBService = require('../db/DBService.js'), PGService = new DBService();

dictRouter
.get('/genders', function (request, response) {
  PGService.performQueryWL(
    DBQueries.selectGendersQuery(),
    PGService.selectRecordsWL,
    function (status, data) {
      return response.status(status)
      .json(data).end();
    }
  );
})
.get('/project-result-types', function (request, response) {
  PGService.performQueryWL(
    DBQueries.selectProjectResultTypesQuery(),
    PGService.selectRecordsWL,
    function (status, data) {
      return response.status(status)
      .json(data).end();
    }
  );
})
.get('/project-result-statuses', function (request, response) {
  PGService.performQueryWL(
    DBQueries.selectProjectResultStatusesQuery(),
    PGService.selectRecordsWL,
    function (status, data) {
      return response.status(status)
      .json(data).end();
    }
  );
})
.get('/remark-statuses', function (request, response) {
  PGService.performQueryWL(
    DBQueries.selectRemarksStatusesQuery(),
    PGService.selectRecordsWL,
    function (status, data) {
      return response.status(status)
      .json(data).end();
    }
  );
})

module.exports = dictRouter;
