'use strict';

var routes = require('express').Router();

routes.get('/', function (request, response) {
  return response.status(200).end();
});
routes.use('/users', require('users'));
routes.use('/projects', require('projects'));
routes.use('/tasks', require('./tasks.js'));
routes.use('/persons', require('persons'));
routes.use('/companies', require('organizations'));
routes.use('/dict', require('dict'));
routes.use('/remarks', require('./remarks.js'));
routes.use('/objects', require('./objects.js'));

module.exports = routes;
