'use strict';

var RequestController = require('express').Router(),
SQLStatements = require('sql-statements'),
PGService = require('pg-service-c');

RequestController
.get('/genders', function (request, response) {
  PGService.selectRecords({
    text: SQLStatements['persons']['ru']['select person genders']
  }, function (status, data) {
    return response.status(status)
    .json(data).end();
  });
})
.get('/project-result-types', function (request, response) {
  PGService.selectRecords({
    text: SQLStatements['dict']['ru']['project result types']['ru']['select project result types']
  }, function (status, data) {
    return response.status(status)
    .json(data).end();
  });
})
.get('/project-result-statuses', function (request, response) {
  PGService.selectRecords({
    text: SQLStatements['dict']['ru']['project result statuses']['ru']['select project result types']
  }, function (status, data) {
    return response.status(status)
    .json(data).end();
  });
})
