'use strict';

var OrganizationsModel = [],
SQLStatements = require('sql-statements'),
PGService = require('pg-service-c');

/** @function select organizations */
PGService.selectRecords({
  text: SQLStatements['organizations']['ru']['select organizations']
}, function (status, data) {
  for (var i = 0; i < data.length; i++) {
    OrganizationsModel.push(data[i]);
  }
});

module.exports = OrganizationsModel;
