'use strict';

var DivisionsModel = [],
SQLStatements = require('sql-statements'),
PGService = require('pg-service-c');

/** @function select divisions */
PGService.selectRecords({
  text: SQLStatements['divisions']['ru']['select divisions']
}, function (status, data) {
  for (var i = 0; i < data.length; i++) {
    DivisionsModel.push(data[i]);
  }
});


module.exports = DivisionsModel;
