'use strict';

var PersonsModel = [],
SQLStatements = require('sql-statements'),
PGService = require('pg-service-c');

/** @function select persons */
PGService.selectRecords({
  text: SQLStatements['persons']['ru']['select persons']
}, function (status, data) {
  for (var i = 0; i < data.length; i++) {
    PersonsModel.push(data[i]);
  }
});

module.exports = PersonsModel;
