'use strict';

var ProjectResultsModel = [],
SQLStatements = require('sql-statements'),
PGService = require('pg-service-c');

PGService.selectRecords({
  text: SQLStatements['projects']['ru']['results']['ru']['select projects results']
}, function (status, data) {
  for (var i = 0; i < data.length; i++) {
    ProjectResultsModel.push(data[i]);
  }
});

module.exports = ProjectResultsModel;
