'use strict';

var UsersModel = require('users');

function getUser(id, cb) {
  for (var i = 0; i < UsersModel.length; i++) {
    if (UsersModel.length > 0 && UsersModel[i].id === id) {
      return cb({
        status: 200,
        user: UsersModel[i]
      });
    } else if (UsersModel.length > 0) {
      return cb({
        status: 204,
        user: []
      });
    }
  }
}

module.exports = getUser;
