var RemarksObjectsRouter = require('express').Router(),
DBQueries = require('../db/DBQueries.js'),
Controller = require('../controller'), PGController = new Controller(),
DBService = require('../db/DBService.js'), PGService = new DBService();

RemarksObjectsRouter
.route('/')
.get(function (request, response) {
  PGService.performQueryWL(
    DBQueries.selectRemarksObjectsQuery(),
    PGService.selectRecordsWL,
    function (status, data) {
      return response.status(status)
      .json(data).end();
    }
  );
})
.post(function (request, response) {
  if (!request.body) {
    return response.status(400).end();
  }
  var remarkObject = {
    objectName: String(request.body.objectName)
  };
  console.log(remarkObject);
  PGService.performQueryWL(
    DBQueries.insertRemarkObjectQuery(remarkObject, 'remarkObjectData'),
    PGService.insertRecordWL,
    function (status, data) {
      return response.status(status)
      .json(data).end();
    }
  );
})

module.exports = RemarksObjectsRouter;
