var assert = require('chai').assert, should = require('chai').should(),
expect = require('chai').expect, request = require('superagent');
LTService = require('pg-service'), PersonsService = new LTService(),
LTServiceC = require('pg-service-c'), PGService = new LTServiceC(),
SQLStatements = require('sql-statements'),

context('Organizations management', function () {
  context('Organizations administrator', function () {
    context('new organization event', function () {
      describe('create organization function', function () {
        describe.skip('create organization with id === 000101500001', function () {
          it('should return organization id === 000101500001', function (done) {
            request('POST', 'http://192.168.1.222/api/organization/')
            .send({
              id: '000101500001',
              shortName: 'Казимпэкс',
              longName: 'АО "РЦ "Казимпэкс"',
              fullName: 'Акционерное общество "Республиканский центр "Казимпэкс"'
            })
            .end(function (err, response) {
              assertl.ok(response, 'response');
              expect(response.status).equals(201);
              expect(Object.keys(response.body).length).equals(1);
              expect(response.body.id).equals('000101500001');
              done();
            });
          });
        });
      });
    });
  });
});
