var PersonsQueries = require('../db/queries/persons.js'),
LTService = require('../test/$pg.js'), PersonsService = new LTService();

var person = {
  id: undefined
};

if (PersonsService.data.length === 0) {
  PersonsService.getRecords({
    text: PersonsQueries['Select persons']
  }).then(
    function (resolve) {
      for (var i = 0; i < resolve.data.length; i++) {
        PersonsService.data.push(resolve.data[i]);
        console.log(PersonsService.data);
      }
    },
    function (reject) {
      console.error(reject.status, reject.message);;
  });
}

PersonsService.getRecord({
  text: PersonsQueries['Select person'],
  values: [person.id]
}).then(
  function (resolve) {
    console.log(resolve.status, resolve.data);
  },
  function (reject) {
    console.error(reject.status, reject.message);;
});

console.log(PersonsService.data);
