var assert = require('chai').assert, should = require('chai').should(),
PersonsQueries = require('../db/queries/persons.js'),
LTService = require('../test/$pg.js'), PersonsService = new LTService();

var id = 1;

describe('User', function () {
  it('should return 1', function () {
    assert.ok(id, 'id is exist');
    assert.typeOf(id, 'number');
    assert.equal(id, 1);
  });
});



describe('PersonsService', function() {
  beforeEach(function () {
    var person = {
      id: '871215301496'
    };
    return PersonsService.getRecord({
      text: PersonsQueries['Select person'],
      values: [person.id]
    }).then(
      function (resolve) {
        return resolve;
      }, function (reject) {
        return reject;
      }
    );
  });
  describe('getPerson', function () {

    it('getPerson should return person with id "871215301496"', function () {
      assert.ok(person);
    });
  });
  
});
