var assert = require('chai').assert, should = require('chai').should(), expect = require('chai').expect,
request = require('superagent');

describe('Restore company by id', function () {
  it('should respond with status code 200 and return company id', function (done) {
    var id = 871215301100;
    request('OPTIONS', 'http://192.168.1.221/api/companies/' + id)
    .end(function (err, response) {
      assert.ok(response, 'response');
      response.status.should.equals(200);
      assert(response.body.id === '871215301100', 'invalid company id');
      done();
    });
  });
});
