var assert = require('chai').assert, should = require('chai').should(), expect = require('chai').expect,
request = require('superagent');


context('Delete and restore person by id', function () {
  describe('Delete person by id', function () {
    it('should respond with status code 200 and return person \'871215301496\'', function (done) {
      var id = '871215301496';
      request('DELETE', 'http://192.168.1.221/api/persons/' + id)
      .end(function (err, response) {
        assert.ok(response, 'response');
        response.status.should.equals(200);
        assert(response.body.id === '871215301496', 'invalid company id');
        done();
      });
    });
  });
  describe('Restore person by id', function () {
    it('should respond with status code 200 and return person \'871215301496\'', function (done) {
      var id = '871215301496';
      request('OPTIONS', 'http://192.168.1.221/api/persons/' + id)
      .end(function (err, response) {
        assert.ok(response, 'response');
        response.status.should.equals(200);
        assert(response.body.id === '871215301496', 'invalid person id');
        done();
      });
    });
  });
});
